import express from 'express';
import {
    getPaths,
    getPath,
    addPopeye,
    deletePopeye,
    updatePopeye
} from '../controllers/paths';

const pathRouter = express.Router();

pathRouter.route('/popeye')
    .get(getPaths)
    .post(addPopeye)

pathRouter.route('/popeye/:id')
    .get(getPath)
    .delete(deletePopeye)
    .put(updatePopeye);


export default pathRouter;

// @desc Get all routes
// @route GET /api/paths
// @access public
import PopeyeRoutes from '../models/popeye';
import "babel-polyfill";
import { io } from '../bin/www';

let interval;
let delay = 1000;

/**
 * Set timer for Routes algorithm delay half of
 * delay of interval for each route.
 *
 * @param {object} socket 
 * @param {object} routes
 * @return {void}
 */
const getApiRouteAndEmit = (socket = {}, routes = {}) => {
    for (let i = 0; i < routes.length; i++) {
        setTimeout(() => {
            socket.emit('geo', routes[i]);
        }, (i + 1) * (delay / 2));
    }
}

/**
 * Set interval for instant real time data through socket
 * 
 * @param {object} socket
 * @param {object} popeyes
 * @return {void}
 */
const setRouteInterval = (socket = {}, popeyes = []) => {
    interval = setInterval(() => {
        getApiRouteAndEmit(socket, popeyes)
    }, delay);
}

/**
 * Fecth all entities.
 *
 * @param {object} req
 * @param {object} res
 * @param {function} next
 */
export const getPaths = async (req, res, next) => {
    try {
        let popeyes = await PopeyeRoutes.find({});

        if (popeyes) {
            res.statusCode = 200;
            res.setHeader('Content-Type', 'application/json');
            io.once('connection', (socket) => {

                socket.on('removeTimer', data => {
                    if(data) {
                        clearInterval(interval);    
                    }
                })

                socket.on('setTimer', data => {
                    if(data) {
                        setRouteInterval(socket, popeyes);    
                    }
                })

                socket.on('milliseconds', milliseconds => {
                    console.log(`Changed the milliseconds for the delay ${milliseconds}`);
                    delay = milliseconds;

                    // Clear interval and reset it.
                    clearInterval(interval);
                    setRouteInterval(socket, popeyes);

                });

                // set interval for routes.
                setRouteInterval(socket, popeyes);

                socket.on('disconnect', () => {
                    clearInterval(interval);
                })
            });
            res.status(200).json(popeyes);
            next();
        } else {
            throw new Error('ERROR: no entities found');
        }
    } catch(error) {
        next(error);
    }
}

/**
 * Fetch entity route by id.
 *
 * @param {object} req
 * @param {object} res
 * @param {function} next
 */
 export const getPath = async (req, res, next) => {
    let { id } = req.params;
    try {
        let popeyeRoute = await PopeyeRoutes.findById({_id: id});

        if (popeyeRoute) {
            res.statusCode = 200;
            res.setHeader('Content-Type', 'application/json');
            res.status(200).json(popeyeRoute);
            next();
        } else {
            throw new Error('ERROR: no entities found');
        }
    } catch(error) {
        next(error);
    }
}


/**
 * Add popeye entity
 * 
 * @param {object} req 
 * @param {object} res 
 * @param {function} next 
 */
export const addPopeye = async (req, res, next) => {
    let { body } = req;

    try {
        let popeye = await PopeyeRoutes.create(body);
        if (popeye) {
            res.statusCode = 200;
            res.setHeader('Content-Type', 'application/json');
            res.status(200).json(popeye);
            next();
        } else {
            throw new Error('Entity was not stored');
        }
    } catch (error) {
        next(error);
    }
}

export const deletePopeye = async (req, res, next) => {
    let { id } = req.params;

    try {
        // Find By id and remove.
        let popeye = await PopeyeRoutes.findByIdAndRemove({_id: id});
        if (popeye) {
            res.statusCode = 200;
            res.setHeader('Content-Type', 'application/json');
            res.status(200).json({
                success: true, 
                message: `Route was successufully deleted popeye ${id}`
            });
            next();
        } else {
            throw new Error('Not found');
        }
    } catch (err) {
        next(err);
    }
}

/**
 * Update entity.
 * 
 * @param {object} req 
 * @param {object} res 
 * @param {function} next 
 */
export const updatePopeye = async (req, res, next) => {
    let { id } = req.params;

    try {
        // Find By id and remove.
        await PopeyeRoutes.findByIdAndUpdate({_id: id}, req.body);
        let popeye = await PopeyeRoutes.findOne({_id: id});

        if (popeye) {
            res.statusCode = 200;
            res.setHeader('Content-Type', 'application/json');
            res.status(200).json(popeye);
            next();
        } else {
            throw new Error('Not found');
        }
    } catch (err) {
        next(err);
    }
}

import mongoose from 'mongoose';
let Schema = mongoose.Schema;

let PopeyeSchema = new Schema({
    name: {
        type: String,
        required: [true, 'Name field is required'],
        unique: true,
        trim: true,
        maxLength: [10, 'Store ID must be less than 10 chars']
    },
    route: {
        type: {
            type: String,
            default: "Feature"
        },
        properties: {
            type: Schema.Types.Mixed, 
            default: {}
        },
        geometry: {
            type : {
                type: String,
                required: [true, 'Coordinates are required'],
                default: 'LineString'
            },
            coordinates: Array
        }
    }
}, { retainKeyOrder: true, minimize: false });

// Sets the created_at parameter equal to the current time
PopeyeSchema.index({geometry : '2dsphere'});
const PopeyeRoutes = mongoose.model('popeye', PopeyeSchema);

export default PopeyeRoutes;

#!/usr/bin/env node

/**
 * Module dependencies.
 */
import app from '../app';
import debugLib from 'debug';
import http from 'http';
const debug = debugLib('popeye-routes:server');

/**
 * Get port from environment and store in Express.
 */

let port = normalizePort(process.env.PORT || '3006');

/**
 * Create HTTP server.
 */
let server = http.createServer(app).listen(port);
var io = require('socket.io')(server, {
	cors: {
		origin: '*',
	}
});

/**
 * Listen on provided port, on all network interfaces.
 */

//server.listen(port);
server.on('error', onError);
server.on('listening', onListening);

/**
 * Normalize a port into a number, string, or false.
 */

function normalizePort(val) {
	let port = parseInt(val, 10);

	if (isNaN(port)) {
		// named pipe
		return val;
	}

	if (port >= 0) {
		// port number
		return port;
	}

	return false;
}

/**
 * Event listener for HTTP server "error" event.
 */

function onError(error) {
	if (error.syscall !== 'listen') {
		throw error;
	}

	var bind = typeof port === 'string'
		? 'Pipe ' + port
		: 'Port ' + port;

	// handle specific listen errors with friendly messages
	switch (error.code) {
		case 'EACCES':
			console.error(bind + ' requires elevated privileges');
			process.exit(1);
			break;
		case 'EADDRINUSE':
			console.error(bind + ' is already in use');
			process.exit(1);
			break;
		default:
			throw error;
	}
}

/**
 * Event listener for HTTP server "listening" event.
 */

function onListening() {
	let addr = server.address();
	let bind = typeof addr === 'string'
		? 'pipe ' + addr
		: 'port ' + addr.port;
	debug('Listening on ' + bind);
}


export {io};
